package com.syntax.classThursday;

public class ShortHandOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int number = 10;
		number=number+1;
		
		System.out.println(number);
		number++;
		System.out.println(number);
		
		number+=10;//number=number+10
		System.out.println(number);
		
		number*=100;
		System.out.println(number);
		
		number=10;
		++number;
		System.out.println(number);
		number++;
		System.out.println(number);
		

	}

}
